	function remtail(str, chr, padding)
	{
	    chr= typeof chr !== 'undefined' ? chr : " ";
		padding = typeof padding !== 'undefined' ? padding : " ";
		f = str.split(chr);
		res = "";
		for (i=0; i < f.length - 1; i++)
		{
			res += f[i] + padding;
		}

		return res;
	}
	
	function fixtail(str, chr)
	{
	    chr= typeof chr !== 'undefined' ? chr : " ";
		f = str.split(chr);
		res = f[0];
		for (i=1; i < f.length - 1; i++)
		{
			res += chr + f[i];
		}

		return res;
	}

