	view.selectize = function (json, fcn, id, title, selectid)
	{

        compo = '<select class="form-control" id="'+selectid+'">'
		for (i=0; i < json.length; i++)
		{
			compo += '<option>' + fcn(json, i) + '</option>';
		}
        compo += '</select>';

		return compo;
	}

